mapper.update:
update product set prod_writer_no = #{product.prodWriterNo}, prod_title = #{product.prodTitle}, prod_start_time = #{product.prodStartTime}, prod_end_time = #{product.prodEndTime}, prod_space_info = #{product.prodSpaceInfo}, prod_goods_info = #{product.prodGoodsInfo}, prod_caution_info = #{product.prodCautionInfo}, prod_maximum_people = #{product.prodMaximumPeople}, prod_address = #{product.prodAddress}, prod_detailed_address = #{product.prodDetailedAddress}, prod_price = #{product.prodPrice}, prod_major_category_id = #{product.prodMajorCategoryId}, prod_subcategory_id = #{product.prodSubcategoryId}, prod_full_address = #{product.prodFullAddress}, prod_location_x = #{product.prodLocationX}, prod_location_y = #{product.prodLocationY}, prod_update_at = NOW()
, file_path = #{product.filePath}
where prod_no = #{product.prodNo};
